import React, { Fragment } from 'react'
import { useForm } from 'react-hook-form'
import { DateTimePicker } from "@material-ui/pickers";

const HookForm = () => {

    const { register, errors, handleSubmit } = useForm();

  const [selectedDate, handleDateChange] = useState(new Date());

    const onSubmit = (data) => {
        console.log('data',data)
        console.log('selectedDate',selectedDate)
    }

    /*
{
	"_name": "Inglish Class",
	"_event_time":"Mon Aug 18 2014 21:11:54 GMT-0400 (hora estándar de Chile)",
	"_time_zone": "UTC",
	"_link_event": "https://discord.com/channels/594363964499165194/594935077637718027"
}
    */

    return (
        <Fragment>
            <h2>Hooks Forms</h2>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input
                    name="_name"
                    ref={
                        register({
                            required: { value: true, message: 'Ingrese el nombre del evento' }
                        })
                    }
                    className="form-control my-2"
                    placeholder="Nombre evento"
                ></input>
                <span className="text-danger text-small d-block mb-2">
                    {errors?._name?.message}
                </span>

                <input
                    name="_link_event"
                    ref={
                        register({
                            required: { value: true, message: 'Ingrese el link por donde se transmitira' }
                        })
                    }
                    className="form-control my-2"
                    placeholder="Link del evento"
                ></input>
                <span className="text-danger text-small d-block mb-2">
                    {errors?._name?._link_event}
                </span>

                <DateTimePicker
                    label="DateTimedataPicker"
                    inputVariant="outlined"
                    value={selectedDate}
                    onChange={handleDateChange}
                />


                <button type="submit" className="btn btn-primary">
                    Enviar
                </button>
            </form>

        </Fragment>
    );
}

export { HookForm };

export default function CreateEvent() {
    return (
        <div>
            <HookForm />
        </div>
    )
};
