import React, { Fragment, useState } from 'react'
import { useForm } from 'react-hook-form'
import DateFnsUtils from '@date-io/date-fns';
import { DateTimePicker, MuiPickersUtilsProvider } from "@material-ui/pickers";
import axios from '../../utils/axios';


const HookForm = () => {

    const { register, errors, handleSubmit } = useForm();

    const [selectedDate, handleDateChange] = useState(new Date());
    const [link, setlink] = useState('')

    const onSubmit = async (data, e) => {
        data._event_time = selectedDate
        const response = await axios.post('api/create', { ...data });
        setlink(`${window.location.origin}/${response.data._nick_name}/${response.data.id}`);        ///:id_owner/:id_counter
        e.target.reset();
    }

    return (
        <Fragment>
            <h2>Hooks Forms</h2>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input
                    name="_name"
                    ref={
                        register({
                            required: { value: true, message: 'Ingrese el nombre del evento' }
                        })
                    }
                    className="form-control my-2"
                    placeholder="Nombre evento"
                ></input>
                <span className="text-danger text-small d-block mb-2">
                    {errors?._name?.message}
                </span>

                <input
                    name="_link_event"
                    ref={
                        register({
                            required: { value: true, message: 'Ingrese el link por donde se transmitira' }
                        })
                    }
                    className="form-control my-2"
                    placeholder="Link del evento"
                ></input>
                <span className="text-danger text-small d-block mb-2">
                    {errors?._name?._link_event}
                </span>

                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                    <DateTimePicker
                        name="_event_time "
                        label="DateTimePicker"
                        inputVariant="outlined"
                        value={selectedDate}
                        onChange={handleDateChange}
                    />
                </MuiPickersUtilsProvider>

                <button type="submit" className="btn btn-primary">
                    Enviar
                </button>
            </form>
            <div>
                <h3> {link}</h3>
            </div>
        </Fragment>
    );
}

const Form = () => {
    const { register, errors, handleSubmit } = useForm();

    const [selectedDate, handleDateChange] = useState(new Date());
    const [link, setlink] = useState('')

    const onSubmit = async (data, e) => {
        data._event_time = selectedDate
        const response = await axios.post('api/create', { ...data });
        setlink(`${window.location.origin}/${response.data._nick_name}/${response.data.id}`);        ///:id_owner/:id_counter
        e.target.reset();
    }

    function showExtra() {
        var element = document.getElementById("extra");
        var btn = document.getElementById("btnExtra");
        element.classList.add("show");
        btn.classList.add("hide");
    }

    return (
        <Fragment>
            <div class="form__event">
                <div class="cont__form">
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <div class="form--input">
                            <input type="text" class="normal" placeholder="Nombre Evento" minlength="5" maxlength="30" required />
                            <span>Nombre Evento</span>
                        </div>

                        <div class="form--input optional">
                            <input type="text" class="normal" placeholder="Descripción" minlength="10" maxlength="30" />
                            <span>Descripción</span>
                            <h5>opcional</h5>
                        </div>

                        <div class="form--input optional">
                            <input type="text" class="normal" placeholder="Categorías" />
                            <span>Categorías</span>
                            <h5>opcional</h5>
                        </div>

                        <div class="form--input half border picture">
                            <input type="file" name="file-4[]" id="file-4" class="normal inputfile inputfile-3" data-multiple-caption="{count} files selected" accept=".jpg, .jpeg, .png, .gif" placeholder="Imagen" required />
                            <label for="file-4">
                                <h6>Imagen</h6>
                            </label>
                            <span>Imagen</span>
                        </div>

                        <div class="form--input half date">
                            <input type="text" class="normal" placeholder="Fecha" required />
                            <span>Fecha</span>
                        </div>
                        <div class="form--input">
                            <input type="text" class="normal" placeholder="Link Evento" required />
                            <span>Link evento</span>
                        </div>

                        <div class="bottom__form">
                            <div class="form--check">
                                <label for="caca">Hey! quiero que sea privado</label>
                                <input type="checkbox" id="caca" checked />
                                <span></span>
                            </div>

                            <div class="opt" onclick="showExtra()" id="btnExtra">Opciones avanzadas</div>


                            <div class="extra" id="extra">
                                <div class="form--check">
                                    <label for="caca">Lorem ipsum dolor sit amet</label>
                                    <input type="checkbox" id="caca" checked />
                                    <span></span>
                                </div>

                                <div class="form--check">
                                    <label for="caca">Consectetur adipisicing elit</label>
                                    <input type="checkbox" id="caca" checked />
                                    <span></span>
                                </div>
                                <div class="form--check">
                                    <label for="caca">Laboriosam eaque ducimus eum autem</label>
                                    <input type="checkbox" id="caca" checked />
                                    <span></span>
                                </div>
                            </div>
                        </div>
                        <button type="submit" class="btn--create">Crear!</button>
                    </form>
                </div>

            </div>
        </Fragment>
    )

}

export { HookForm };

export default function CreateEvent() {
    return (
        <main>
            <Form />
            <div class="bg__full"></div>
        </main>
    )
};
