import React, { Component, useEffect, useState } from 'react'
import { google, outlook, office365, yahoo, ics } from "calendar-link";


const Calendars = ({ name, timeTZ }) => {

    const [browser, setBrowser] = useState()

    const event = {
        title: name,
        start: timeTZ
    };

    const openCalendar = (link) => {
        window.open(link, "_blank")
    }

    const sw = navigator.serviceWorker;

    useEffect(() => {
        console.log(1)
        console.log('sw', sw)
        if (sw && browser == true) {
            console.log(2)
            /*global showNotification, TimestampTrigger*/
            /*eslint no-undef: "error"*/
            const requestServices = async () => {
                const reg = await navigator.serviceWorker.getRegistration();
                Notification.requestPermission().then(permission => {
                    if (permission !== 'granted') {
                        alert('you need to allow push notifications');
                    } else {
                        const timestamp = new Date().getTime() + 10 * 1000;
                        const scheduledTime = new Date(timestamp);
                        reg.showNotification(
                            'Scheduled Push Notification',
                            {
                                tag: timestamp, // a unique ID
                                body: 'Hi there, it\'s ' + pad(scheduledTime.getHours()) + ':' + pad(scheduledTime.getMinutes()), // content of the push notification
                                showTrigger: new TimestampTrigger(timestamp), // set the time for the push notification
                                data: {
                                    url: window.location.href, // pass the current url to the notification
                                }
                            }
                        );
                        $button.setAttribute('disabled', 'disabled');
                    }
                });
            }
            requestServices();

        }
    }, [sw, browser])

    return (
        <div className="calendars">
            <p>También, recuerdame</p>
            <ul>
                <li onClick={() => openCalendar(google(event))}>
                    <div className="ic google"></div>
                    <div className="info">
                        <span>Calendar</span>
                        <h4>Google</h4>
                    </div>
                </li>
                <li onClick={() => openCalendar(ics(event))}>
                    <div className="ic apple"></div>
                    <div className="info">
                        <span>Calendar</span>
                        <h4>Apple</h4>
                    </div>
                </li>
                <li onClick={() => openCalendar(outlook(event))}>
                    <div className="ic outlook"></div>
                    <div className="info">
                        <span>Calendar</span>
                        <h4>Outlook</h4>
                    </div>
                </li>
            </ul>

            <div className="browser" onClick={() => setBrowser(true)}>
                <p>Nah! prefiero en mi navegador</p>
                <div className="ic chrome"></div>
            </div>

            <p className="cright">Lorem ipsum dolor, sit amet consectetur adipisicing elit. Fugit a ipsum quod eveniet excepturi.</p>
        </div>
    )
}

export { Calendars }
